public class LogestPalindronicSubstring {
    public static void main(String[] args){
//        String s="a";
//        String s="";
        String s="";
        System.out.println(getResult(s));
    }
    public static String getResult(String s){
        String result="";
        for(int i=0;i<s.length();i++){
            int temp=s.length()-1;
            String newString="";
            for(int j=temp;j>=i;){
                newString+=s.charAt(j);
                if(i==j){
                    if(newString.equals(s.substring(i,temp+1)) && result.length() < newString.length()){
                        result=newString;
                    }
                    newString="";
                    temp--;
                    j=temp;
                    if (temp==i-1){
                        break;
                    }
                    continue;
                }
                j--;
            }
        }
        return result;
    }
}
