public class LongestpalindronicSubstring2 {
    public static void main(String[] args){
//        String s="a";
//        String s="";
        String s="";
        System.out.println(getResult(s));
    }
    public static String getResult(String s){
        String result="";
        for(int i=0;i<s.length();i++){
            int temp=s.length()-1;
            String rs=getSubstring(s,i,temp);
            for (int j=0;j<rs.length();j++){
                String ss=rs.substring(j);
                if(ss.equals(s.substring(i,temp+1)) && result.length() < ss.length()){
                    result=ss;
                }
                temp--;
            }
        }
        return result;
    }
    public static String getSubstring(String s,int i,int temp){
        String result="";
        for(int j=temp;j>=i;j--){
            result+=s.charAt(j);
        }
        return result;
    }
}

